(function() {
  Mousetrap.bind('f r', function() {
    return strip.randFg();
  });

  Mousetrap.bind('b r', function() {
    return strip.randBg();
  });

  Mousetrap.bind('d', function() {
    return strip.resetColors();
  });

  Mousetrap.bind('r', randomizePalette);

  Mousetrap.bind('mod+z', function() {
    return strip.cancelLast();
  });

  Mousetrap.bind('n n n', function() {
    return strip.resetCanvas();
  });

  Mousetrap.bind('up', function() {
    return strip.sizeUp();
  });

  Mousetrap.bind('down', function() {
    return strip.sizeDown();
  });

  Mousetrap.bind('m', toggleMenu);

  Mousetrap.bind('esc', toggleMenu);

}).call(this);
